    public static ArrayList<WisataModel> getListDataWisata() {
        ArrayList<WisataModel> list = new ArrayList<>();
        for (String[] wisataData : dataWisata) {
            WisataModel wisataModel = new WisataModel();

            wisataModel.setNamaWisata(wisataData[0]);
            wisataModel.setNamaLokasiWisata(wisataData[1]);
            wisataModel.setDescWisata(wisataData[2]);
            wisataModel.setGambarWisata(wisataData[3]);

            list.add(wisataModel);

        }
        return list;
    }
